(382, 'Germaine', 'Koh', NULL, 'Germaine Koh\'s work involves the emphasis on everyday actions, familiar objects and common places. Koh was a recipient of the 2010 VIVA Award.', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=395', NULL, 2),
(383, 'Sonny', 'Assu', 'http://sonnyassu.com/', 'Sonny Assu grew up in North Delta, BC, away from his ancestral home in Cape Mudge and Kwakwaka\'wakw culture. He graduated from Emily Carr University in 2002. In an exploration of his mixed ancestry, he works across disciplines and appropriates and transforms items of consumer and popular culture and First Nations iconography.', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=396', 'https://covapp.vancouver.ca/PublicArtRegistry/ImageDisplay.aspx?AreaId=396&ImageId=551', 2),
(384, 'Oscar', 'Maltipi', NULL, NULL, 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=397', NULL, NULL),
