(489, 'Offsite - 8 Days', 'Removed', '2013-01-01', 'Shangri-la - Offsite', '1128 West Georgia Street', '\"8 Days\" is a collection of recent films that brings the viewer to the streets of Toronto, London and Beirut. One film is screened per day on an eight-day rotation. Some of the films isolate everyday moments and emphasize them with a deliberate slowness. Other films are deconstructed by way of its own components.', NULL, NULL, NULL, NULL, 'https://covapp.vancouver.ca/PublicArtRegistry/ImageDisplay.aspx?AreaId=562&ImageId=829', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtworkDetail.aspx?ArtworkId=562', 1, 4, 14),
(490, 'Rose Cole Yelton Memorial Pole', 'In Place', '2009-01-01', 'Brockton Point', 'Stanley Park', 'This pole was raised in 2009 to honor Rose Cole Yelton, her family, and everyone who lived in Stanley Park. The pole is situated in front of the house site where the Cole family lived until 1935.', NULL, NULL, NULL, NULL, 'https://covapp.vancouver.ca/PublicArtRegistry/ImageDisplay.aspx?AreaId=563&ImageId=458', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtworkDetail.aspx?ArtworkId=563', 11, 1, 10),
