(500, 'Ninth Column', 'In Place', '2014-01-01', 'CENTRAL', '1618 Quebec Street', 'Ninth Column comprises a 97-foot high structural steel column clad in custom cast panels which bear an exact replica of mature Douglas-fir bark. This column is of equal scale as the other eight structural concrete columns supporting the overhead building.', NULL, '49.271536', '-123.101402', 'Structural steel, polymerized gypsum (Matrix-G), concrete, stainless steel, aluminum', NULL, 'https://covapp.vancouver.ca/PublicArtRegistry/ArtworkDetail.aspx?ArtworkId=574', 3, 7, 12),
(501, 'Offsite: For Everyone A Sunset', 'Removed', '2014-01-01', 'Shangri-la - Offsite', '1128 West Georgia Street', '\"For Everyone a Sunset\" is an outdoor light installation that transforms ordinary materials such as aluminum, glass and neon to create plays of light that activate the surrounding space. Each module creates compositions of coloured light that are further augmented by changes in natural light throughout the day.', NULL, '49.285762', '-123.123947', NULL, 'https://covapp.vancouver.ca/PublicArtRegistry/ImageDisplay.aspx?AreaId=575&ImageId=811', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtworkDetail.aspx?ArtworkId=575', 1, 7, 12),
