(172, 'Bernie', 'Miller', NULL, 'Bernie Miller is an artist who has exhibited his sculptures across Canada and internationally since 1975. He is interested in architectural form, public monuments, mechanical means, and social meaning. His work is represented by the Sable Castelli Gallery in Toronto.', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=177', NULL, NULL),
(173, 'Claudia', 'Cuesta', NULL, 'Claudia Cuesta was born in Colombia and educated in Britain and Canada. She has taught workshops and art classes at the Emily Carr College of Art and Design, the Vancouver Art Gallery, and the Universidad Nacional, Medellin, Colombia. Her work has been exhibited internationally and she has done private and Corporate Commissions in Canada, USA, Britain, Columbia, Mexico, France, Chile, Italy, and Russia.', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=178', NULL, NULL),
