(452, 'Kathy', 'Slade', NULL, NULL, 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=466', NULL, NULL),
(453, 'Jeremy', 'Borsos', NULL, 'Jeremy Borsos lives and works on Mayne Island, British Columbia and in Berlin, Germany. He enrolled at the Emily Carr School of Art in 1983 before moving to New York, where he lived and attended the Arts Students League. He has had numerous regional, national and international exhibitions of his artworks. These works investigate various media as artefact and the ways they are used to interpret the past.', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=467', NULL, NULL),
(454, 'Melanie', 'Schambach', 'http://www.MelanieSchambach.com', 'Melanie facilitates participatory painting processes that encourage participants to nurture their voices, open dialogue, and think critically on social justice issues relevant to their communities. The collective outcomes are large-scale paintings/murals that communicate stories and messages provoking public reflection and awareness.', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=468', NULL, NULL),
(455, 'Other Sights for Artists\'', 'Projects', 'http://www.othersights.ca/', 'Incorporated in May, 2005 as a non-profit society, Other Sights operates as a collective of Vancouver-based individuals with expertise in curation, project management, presentation, delivery and promotion of temporary art projects in public spaces. Other Sights is dedicated to challenging perceptions, encouraging discourse and promoting individual perspectives about shared social spaces. Other Sights seeks to create a presence for art in spaces and sites that are accessible to a broad public, such as the built environment, communications technologies, the media, and the street.Operating outside of the gallery context, Other Sights develops new and unexpected exhibition platforms and provides support to artists, writers and curators interested in creating temporary, critically rigorous work for highly visible locations.  They collaborate and share resources with organizations and individuals in order to present projects that consider the aesthetic, economic and regulatory conditions of public places and public life.', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=469', NULL, NULL),
