(541, 'Katie', 'Maasik', NULL, NULL, 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=559', NULL, NULL),
(542, 'Khan', 'Lee', 'http://www.khanlee.com', 'Khan Lee was born in Seoul, Korea. He studied architecture at Hong-Ik University, before immigrating to Canada to study fine art at Emily Carr University of Art and Design. Through sculptural and media practices, his work attempts to exhibit results of experimentation with form and process in order to express inherent relationships between material and immaterial content. He is a founding member of the Vancouver-based artist collective Intermission and is presently a member of the Instant Coffee artist collective. His work has been exhibited nationally and internationally. Lee lives and works in Vancouver, BC.', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=560', 'https://covapp.vancouver.ca/PublicArtRegistry/ImageDisplay.aspx?AreaId=560&ImageId=1234', NULL),
(543, 'Shannon', 'Oksanen', NULL, NULL, 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=561', NULL, NULL),
(544, 'Chris', 'Doray', NULL, NULL, 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=562', NULL, NULL),
(545, 'Jay', 'Havens', 'http://www.jayhavens.me/', 'Jay Havens is a multidisciplinary artist of Haudenosaunee-Mohawk and Scottish-Canadian ancestry and member belonging to the Six Nations of the Grand River, Mohawk Bear Clan. He works accross Turtle Island as a freelance designer, artist in residence, and educator for production companies, post-secondary institutions and school districts alike. Havens is most interested in opportunities which inform audience ideas around Indigeneity. ', 'https://covapp.vancouver.ca/PublicArtRegistry/ArtistDetail.aspx?FromArtistIndex=False&ArtistId=565', NULL, NULL),
